import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Client_Current_Product from "./Client_Current_Product";
import Client_Product_In_Cart from "./Client_Product_In_Cart";
import Client_Nav from "./Client_Nav";

export default function Client_Shoping_Cart(props) {
    const [products, setProducts] = useState([{}]);
    const [orderid, setOrderid] = useState([]);
    const [proinstore, setproinstore] = useState([]);
    const [outOfStock, setOutOfStock] = useState([]);
    const [belowStock, setBelowStock] = useState([]);
    const [quantity, setquantity] = useState([]);

    // products.QuantityItem
    let navigate = useNavigate();
    let location = useLocation();
    useEffect(() => {
        let myquantity = [];
        let Allitemss = location.state.products;
        setProducts(Allitemss);
        checkbtns();
        Allitemss.forEach(element => {
            myquantity.push({ quantity: element.QuantityItem, itemname: element.ItemName });
        })
        setquantity(myquantity);
        return async () => {
            let ans = await fetch("http://localhost:3678/cart/quantity", {
                method: 'PUT',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    quantity: quantity
                })
            })
            ans = await ans.json();
        }
    });
    async function checkbtns() {
        let arrm = [];
        let arrp = [];

        let pro_in_cart_and_store = fetch(`http://localhost:3678/products/itemname/${products}`, {
            method: 'GET'
        })
        pro_in_cart_and_store = (await pro_in_cart_and_store).json();
        setproinstore(pro_in_cart_and_store);
        products.forEach(element => {
            pro_in_cart_and_store.forEach(proelement => {
                if (element.ItemName === proelement.ItemName) {
                    if (element.QuantityItem + 1 >= proelement.QuantityItem) {
                        arrp.push(true);
                    } else {
                        if (element.QuantityItem - 1 < 0) {
                            arrm.push(true);
                        } else {
                            arrp.push(false);
                            arrm.push(false);

                        }
                    }
                }
            });
        });
        setOutOfStock(arrp);
        setBelowStock(arrm);
    }

    async function forPaying() {
        navigate("/client/finalBill", { state: { products: products, orderid: orderid } });
    }
    async function increaseQ(i, myproduct) {
        let arrm = belowStock;
        let arrp = outOfStock;
        let arrq = quantity;
        arrq[i - 1].quantity += 1;
        setquantity(arrq);
        proinstore.forEach(proelement => {
            if (myproduct.ItemName === proelement.ItemName) {
                if (myproduct.QuantityItem + 1 > proelement.QuantityItem) {
                    arrp[i] = true;
                } else {
                    if (myproduct.QuantityItem - 1 < 0) {
                        arrm[i] = true;
                    } else {
                        arrp[i] = false;
                        arrm[i] = false;

                    }
                }
            }
        });

        setOutOfStock(arrp);
        setBelowStock(arrm);
    }
    async function reduceQ(i, myproduct) {
        let arrm = belowStock;
        let arrp = outOfStock;
        let arrq = quantity;
        arrq[i - 1].quantity -= 1;
        setquantity(arrq);
        proinstore.forEach(proelement => {
            if (myproduct.ItemName === proelement.ItemName) {
                if (myproduct.QuantityItem + 1 > proelement.QuantityItem) {
                    arrp[i] = true;
                } else {
                    if (myproduct.QuantityItem - 1 < 0) {
                        arrm[i] = true;
                    } else {
                        arrp[i] = false;
                        arrm[i] = false;

                    }
                }
            }
        });

        setOutOfStock(arrp);
        setBelowStock(arrm);
    }
    async function removeItem(i, myproduct) {
        let curquantity = quantity;
        curquantity.splice(i, 1);
        setquantity(curquantity);
        let arr = JSON.parse(sessionStorage.getItem("currentuser"));
        let cartarr = arr.cart;
        cartarr.splice(i, 1);
        sessionStorage.setItem("currentuser", JSON.stringify({ password: arr.password, user_name: arr.user_name, cart: cartarr }));
        setProducts(cartarr);
        // let produc = await fetch(`http://localhost:3678/products/itemname/${code}`, {
        //     method: 'GET'
        // });
        // let mypro = await produc.json();
        // if (mypro) {
        let prod = await fetch(`http://localhost:3678/cart/deleteitem`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ myproduct: myproduct })
        });
        let pro = await prod.json();
        // } else {
        // alert("cannot delete item.item is Ordered by client and still not delivered");
        // }
        // getProducts();

    }
    return (<div>
        <Client_Nav />
        <p>shoping cart</p>
        {products.map((myproduct, index) =>
        (
            <div key={index}>
                <br></br>
                {myproduct.ItemName}
                <p>quantity: </p>
                <p>{quantity[index].quantity}</p>
                {console.log(quantity[index].quantity)}
                {/* <Client_Product_In_Cart product={myproduct} Allitems={products} /> */}
                <button disabled={outOfStock[index]} onClick={() => { increaseQ(index + 1, myproduct) }}>+</button>
                <button disabled={belowStock[index]} onClick={() => { reduceQ(index - 1, myproduct) }}>-</button>
                <button onClick={() => { removeItem(index, myproduct) }}>remove</button>
            </div>
        ))}
        <button onClick={forPaying}>to pay</button>
    </div>);

}
