import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Client_Shoping_Cart from "./Client_Shoping_Cart";
export default function final(props) {
    let location = useLocation();
    let orderid = location.state.orderid[0];

    async function orderConfirmation() {
        //להעביר התראה על הזמנה למנהל
        let itemIC = await fetch(`http://localhost:3678/orders/setOrder`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                // CodeItem: product.CodeItem,
                 OrderID:orderid,
                 CustomerID: 56789
                //JSON.parse(sessionStorage.getItem("currentuser")).password;
            })
        });
    }


    return (<div>
        <p>your items:</p>
        {location.state.products.map((myproduct) =>
        (<div key={myproduct.CodeItem}>
            {myproduct.ItemName}
            <p>quantity: </p><p>{location.state.quantity}</p>
        </div>)
        )}
        <button onClick={orderConfirmation}>For order confirmation</button>
    </div>);
}
