import React, { useState, useEffect } from "react";
import Nav from "./Nav"
import '../App.css';

export default function Manager_Orderding_List(props) {
    const [orders, setOrders] = useState([{}]);
    useEffect(() => {
        let a=[];
        setOrders(a)
        async function getAllOrders() {
            let prod = await fetch("http://localhost:3000/orders/ordered", {
                method: 'GET'
            });
            let pro = await prod.json();
            setOrders(pro);


        }
        getAllOrders();
    }, []);

    async function updateOrder(order) {
        let ans = await fetch("http://localhost:3000/orders/updateOrdered", {
            method: 'PUT',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                OrderCode:order.OrderCode

            })
        });
        ans = await ans.json();
    }

    async function isChecked(order)
    {
        debugger;
        let index=orders.indexOf(order)
       orders.splice(index, 1);
        setOrders(orders);
        updateOrder(order);
    }


    return (<div>
        <Nav />
        <br /><br /><br />
        {orders.map((order,index) =>
            (<div key={index}>
                <p>order code:</p>
                {order.OrderCode}
                <p>date:</p>
                {order.DateOrder}
                <p>client details:</p>
                <p>name:</p>
                {order.FirstName}
                <p>adress:</p>
                {order.Address}
                <p>payment details</p>
                <p>total payment:</p>
                {order.OrderPrice}
                <p>credit card number:</p>
                {order.CreditCard}
                <input type="checkbox" id="myCheck" onClick={(e)=>{isChecked(order)}}></input>
            </div>)
        )}
    </div>);
}
