import { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../App.css';

let date = new Date();
function Sign_In(props) {
    const [password_value, setPassword_value] = useState("");
    const [user_name_value, setUser_name_value] = useState("");
    let navigate = useNavigate();
    const [current, setcuurent] = useState("");

    let a;
    function savePasswordValue(val) {
        setPassword_value(val);
    }

    function saveUsernameValue(val) {
        setUser_name_value(val);
    }

    // async function getfetch(url) {
    //     try {
    //         let res = await fetch(url);
    //         return await res.json();
    //     } catch (error) {
    //         console.log(error);
    //     }
    // }

    async function checkIntegrity() {
        let myuser;
        try {
            if (password_value === '' || user_name_value === "") {
                alert("password or username are empty");
            } else {
                debugger
                // let users = await getfetch(`https://localhost:3000/customers`);//bring array of customers that hav this specific name/${user_name_value}
                let flag = await fetch(`http://localhost:3000/getcustomers`, {
                    method: 'POST',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ username: user_name_value, password: password_value })
                });
                flag = await flag.json();
                // let flag = false;
                // if(users)
                // {
                //     users.forEach((element, i) => {
                //     if ((users[i].costemerID) === password_value) {
                //         flag = true;
                //         myuser=users[i];
                //     }
                //  });
                // }

                if (!flag) {
                    // alert("the password or username do nnnnot exist");

                    fetch(`http://localhost:3000/customers`, {
                        method: 'POST',
                        headers: {
                            'Accept': 'application/json',
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            username: user_name_value,
                            password: password_value
                        })
                    });
                    navigate(`/manager`);
                    sessionStorage.setItem("currentuser", JSON.stringify({ password: password_value, user_name: user_name_value }));
                    a = sessionStorage.getItem("currentuser");
                    console.log(JSON.parse(a));
                    // navigate(`/Client/${password_value}`);
                    // alert("the password or username do not exist");
                } else {
                    alert("the password or username  exist");
                    //  fetch(`http://localhost:3000/customers?username=${user_name_value}`,{
                    //      method:'POST'});

                    // if (localStorage.getItem("user")) {
                    // navigate("/our_client");
                    // }
                    // else {
                    //     localStorage.setItem("user", JSON.stringify(users));
                    //     props.define_user(users);
                    //     navigate("/user");
                    // }
                }
            }

        } catch (error) {
            alert(error);
        }
        savePasswordValue("");
        saveUsernameValue("");
    }
    if (!a) {
        return (
            <div>
                <div id="hero-background"></div>
                <div id="main-wrapper">
                    <div className="form-section">
                        <div className="contain-wrapper">
                            <div className="form">
                                <div className="form-header">
                                </div>
                                <div className="form-contain">
                                    <button onClick={() => { navigate("/"); }}>login</button>
                                    <input type="input" className="form-control" value={user_name_value} onChange={(e) => { saveUsernameValue(e.target.value) }} id={date} placeholder="username"></input>
                                    <input type="password" className="form-control" value={password_value} onChange={(e) => { savePasswordValue(e.target.value) }} id={date} placeholder="password"></input>
                                    <div className="btn" onClick={checkIntegrity}>
                                        <input type="button" value=" sign in" />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Sign_In;
