import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import Manager_Product_List from "./Manager_Product_List";
import Manager_Add_Product from "./Manager_Add_Product";
import Manager_Orderding_List from "./Manager_Orderding_List";
import Manager_Profits from "./Manager_Profits";
import { Link, Outlet } from "react-router-dom"

export default function My_Manager(props) {
    let navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const [client, setClient] = useState([]);
    const [category, setCategory] = useState("");
    const [mystyle, setMystyle] = useState({});
    useEffect(() => {
        async function getProducts() {
            let prod = await fetch("http://localhost:3000/products", {
                method: 'GET'
            });
            let pro = await prod.json();
            setProducts(pro);
            setMystyle({
                color: "#3aedff",
                fontSize: "20px", marginLeft: "330px",
                marginTop: "70PX"
            });
        }
        getProducts();
    }, []);

    // async function categoryType(val) {
    //     setCategory(val);
    //     console.log(`val ${val}`);
    //     let codeC = await fetch(`http://localhost:3000/products/${val}`, {
    //         method: 'GET'
    //     });
    //     let codeCateg = await codeC.json();

    //     let catItems = await fetch(`http://localhost:3000/products/category/${codeCateg[0].CategoryCode}`, {
    //         method: 'GET'
    //     });
    //     let allCatItems = await catItems.json();
    //     console.log(allCatItems);
    //     setProducts(allCatItems);
    // };

    function sentocomponent(num) {
        switch (num) {
            case 1:
                navigate("productlist", { state: {products:products} });
                break;
            case 2:
                navigate("addProduct");
                break;
            case 3:
                navigate("orderingList");
                break;
            case 4:
                navigate("profits");
                break;
        }
    }

    return (<div>
        {/* <p>all items</p> */}
        {/* <div>{products.map((product) => product.ItemName)}</div> */}
        <br></br>
        {/* <label>categories: </label> */}
        {/* <select onChange={(e)=>{setCategory(e.target.value)}}> */}
        {/* <select onChange={(e) => { categoryType(e.target.value) }}> */}
            {/* <option>heels</option> */}
            {/* <option>flat</option> */}
            {/* <option>sneakers</option> */}
            {/* <option>sandals</option> */}
            {/* <option>boots</option> */}
            {/* <option>slippers</option> */}
        {/* </select><br></br> */}
        <button style={mystyle} onClick={() => { sentocomponent(1) }} >Product List</button>
        <button style={mystyle} onClick={() => { sentocomponent(2) }} >Add Product</button>
        <button style={mystyle} onClick={() => { sentocomponent(3) }} >Orderding List</button>
        <button style={mystyle} onClick={() => { sentocomponent(4) }} >Profits</button>
        <div>
                    <nav id="navs">
                        <Link className="link" to={`/productlist`}>productlist</Link>
                        <Link className="link" to={`/addProduct`}>addProduct</Link>
                        <Link className="link" to={`/orderingList`}>orderingList</Link>
                        <Link className="link" to={`/profits`}>profits</Link>
                      </nav>
                    <Outlet />
                </div>

        {/* <Manager_Product_List products={products} /> */}
        {/* <Manager_Add_Product /> */}
        {/* <Manager_Orderding_List /> */}
        {/* <Manager_Profits /> */}

    </div>);
}
