import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";
import Nav from "./Nav"
import '../App.css';

export default function Manager_Add_Product(props) {
    const [product_name, setproductname] = useState();
    const [product_description, setproductdescription] = useState();
    const [image, setImage] = useState({ preview: '', data: '' });
    const [status, setStatus] = useState('');
    const [truf, settruf] = useState(true);
    const [amount_of_units, setamountofunits] = useState();
    const [catagory, setcatagory] = useState();
    const [code, setcode] = useState();
    const [price, setprice] = useState();

    function App() {
        
        const handleSubmit = async (e) => {
            e.preventDefault();
            let formData = new FormData();
            formData.append('file', image.data);
            const response = await fetch('http://localhost:3000/image', {
                method: 'POST',
                body: formData,
            });
            if (response) {
                setStatus(response.statusText);
            }
        }

        const handleFileChange = (e) => {
            const img = {
                preview: URL.createObjectURL(e.target.files[0]),
                data: e.target.files[0],
            }
            setImage(img);
            settruf(true);
        }
        return (
            <div className='App'>
                <h1>insert a picture:</h1>
                {truf&&image.preview && <img src={image.preview} width='100' height='100' />}
                <hr></hr>
                <form onSubmit={handleSubmit}>
                    <input type='file' name='file'  onChange={handleFileChange}></input>
                    <button className="btn btn-outline-secondary btn-lg" type='submit'>download</button>
                </form>
                {truf&&status && <h4>{status}</h4>}
            </div>);
    }



    function saveproductname(product) {
        setproductname(product);
    }

    function saveproductdescription(text) {
        setproductdescription(text);
    }
function saveamountofunits(units){
    setamountofunits(units);
}
function savecatagory(catagory){
    setcatagory(catagory);
}
function savecode(code){
    setcode(code);
}
function saveprice(price){
    setprice(price);
}
    async function save_new_product() {
     let ans=await fetch("http://localhost:3000/product/save",{
            method:'POST',
            headers: {'Accept': 'application/json',
             'Content-Type': 'application/json'
           },
            body:JSON.stringify({
                name:product_name,
                description:product_description,
                path:image.preview,
                amount:amount_of_units,
                category:catagory,
                code:code,
                price:price
            })
        })
         ans=await ans.json();
        console.log(ans);
        settruf(false);
        saveproductname('');
      setproductdescription('');
      setamountofunits('');
      setcatagory('');
      setcode('');
      setprice('');

    }
    // value={product_name}
    return (<div>
        <Nav></Nav>
        <form>
        <h4 className="">put in a product name</h4>
        <input type="input" minLength="2" className="" value={product_name} onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h4>put in the amount of units</h4>
        <input type="input" minLength="1" className="" value={amount_of_units} onChange={(e) => { saveamountofunits(e.target.value) }} placeholder="amount of units"></input>
        <h4>catagory</h4>
        <input type="input" minLength="1" className="" value={catagory} onChange={(e) => { savecatagory(e.target.value) }} placeholder="catagory"></input>
        <h4>code of product</h4>
        <input type="input" minLength="1"className="" value={code} onChange={(e) => { savecode(e.target.value) }} placeholder="code of product"></input>
        <h4>price</h4>
        <input type="text" minLength="1" className="" value={price} onChange={(e) => { saveprice(e.target.value) }} placeholder="price"></input>
        <h4>put in a product description</h4>
        <textarea className="" minLength="2" value={product_description} onChange={(e) => { saveproductdescription(e.target.value) }} rows="10" cols="50" placeholder="product description"></textarea >
        {/* <input type="reset" value="Reset"/> */}
        </form>
        <br></br>
        {App()}
        <br></br>
        <button  className="btn btn-outline-secondary btn-lg" onClick={save_new_product}>save</button>
    </div>)
}
