                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ password: JSON.parse(sessionStorage.getItem("currentuser")).password })
            });
            let pro = await prod.json();
            setCart(pro);
        }
        async function showItems() {
            itemCode = await props.product.CodeItem;
            if (itemCode) {
                AllItems = props.Allitems;
                item = AllItems.filter(i => i.CodeItem == itemCode);
                itemQ = item[0].QuantityItem;
            }
        }


