import React, { useState } from "react";
import { useLocation } from 'react-router-dom';
export default function Manager_Product_List(props) {
    const location = useLocation();
    async function buymyproduct(myproduct) {

        let prod = await fetch("http://localhost:3000/client{}/shopingCart", {
            method: 'POST',
            body: { myproduct }
        });
        let pro = await prod.json();
    }
    async function categoryType(val) {
        setCategory(val);
        console.log(`val ${val}`);
        let codeC = await fetch(`http://localhost:3000/products/${val}`, {
            method: 'GET'
        });
        let codeCateg = await codeC.json();

        let catItems = await fetch(`http://localhost:3000/products/category/${codeCateg[0].CategoryCode}`, {
            method: 'GET'
        });
        let allCatItems = await catItems.json();
        console.log(allCatItems);
        setProducts(allCatItems);
    };
    return (
        <div>
            <p>all items</p>
        {/* <div>{products.map((product) => product.ItemName)}</div> */}
        <br></br>
        <label>categories: </label>
        {/* <select onChange={(e)=>{setCategory(e.target.value)}}> */}
        <select onChange={(e) => { categoryType(e.target.value) }}>
            <option>heels</option>
            <option>flat</option>
            <option>sneakers</option>
            <option>sandals</option>
            <option>boots</option>
            <option>slippers</option>
            </select>
            <br></br>{location.state.products.map((myproduct) =>
                (<div key={myproduct.ItemName}>
                     {myproduct.ItemName}
               {/* <button>add to shopping cart</button> */}
               </div>)
            )}
            {/* <button onClick={(e) => { buymyproduct(e.target.value) }}>ADD TO SHOPING CART</button> */}
        </div>
    )



}
