import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";
import Nav from "./Nav"

export default function Manager_Add_Product(props) {
    const [product_name, setproductname] = useState();

    function App() {
        const [image, setImage] = useState({ preview: '', data: '' });
        const [status, setStatus] = useState('');
        const handleSubmit = async (e) => {
            e.preventDefault();
            let formData = new FormData();
            formData.append('file', image.data);
            const response = await fetch('http://localhost:3000/image', {
                method: 'POST',
                body: formData,
            });
            if (response) {
                setStatus(response.statusText);
            }
        }

        const handleFileChange = (e) => {
            const img = {
                preview: URL.createObjectURL(e.target.files[0]),
                data: e.target.files[0],
            }
            setImage(img);
        }
        return (
            <div className='App'>
                <h1>insert a picture:</h1>
                {image.preview && <img src={image.preview} width='100' height='100' />}
                <hr></hr>
                <form onSubmit={handleSubmit}>
                    <input type='file' name='file' onChange={handleFileChange}></input>
                    <button type='submit'>download</button>
                </form>
                {status && <h4>{status}</h4>}
            </div>);
    }



    function saveproductname(product) {
        setproductname(product);
    }

    function save_new_product(){
        
    }
    // value={product_name}
    return (<div>
        <h1>put in a product name</h1>
        <input type="input" className="form-control" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h1>put in a product description</h1>
        <textarea className="form-control"  rows="15" cols="50" placeholder="product description"></textarea >
        <br></br>
        {App()}
        <br></br>
        <button onClick={save_new_product}>save</button>
    </div>)
}
