import React, { Component, useEffect } from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

//import React, { useState,useEffect } from "react";
import Client_Product_List from "./Client_Product_List";
import Client_Shoping_Cart from "./Client_Shoping_Cart";

export default function Our_Client(props) {
    const [products, setProducts] = useState([]);
    const [client, setClient] = useState([]);
    const [category, setCategory] = useState("");
    useEffect(() => {
        async function getProducts() {
            let prod = await fetch("http://localhost:3000/products", {
                method: 'GET'
            });
            let pro = await prod.json();
            setProducts(pro);
            // let mclient = await fetch("http://localhost:3000/client", {
            //     method: 'GET'
            // });
            // let cli = await mclient.json();
            // setClient(pro);
            //get products from db and save in usestate
        }
        getProducts();
    }, []);

    async function categoryType(val) {
        
        setCategory(val);
        console.log(`val ${val}`);
        let codeC = await fetch(`http://localhost:3000/products/${val}`, {
            method: 'GET'
        });
        let codeCateg = await codeC.json();
        
        let catItems=await fetch(`http://localhost:3000/products/category/${codeCateg[0].CategoryCode}`, {   
            method: 'GET' 
        });
        let allCatItems = await catItems.json();
         console.log(allCatItems);
         setProducts(allCatItems);
    };


    return (<div>
        <p>all items</p>
        {/* <div>{products.map((product) => product.ItemName)}</div> */}
        <br></br>
        <label>categories: </label>
        {/* <select onChange={(e)=>{setCategory(e.target.value)}}> */}
        <select onChange={(e) => { categoryType(e.target.value) }}>
            <option>heels</option>
            <option>flat</option>
            <option>sneakers</option>
            <option>sandals</option>
            <option>boots</option>
            <option>slippers</option>
        </select><br></br>
        <Client_Product_List products={products} />
        <Client_Shoping_Cart />
    </div>);
}
