import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import Manager_Product_List from "./Manager_Product_List";
import Manager_Add_Product from "./Manager_Add_Product";
import Manager_Orderding_List from "./Manager_Orderding_List";
import Manager_Profits from "./Manager_Profits";
import { Link, Outlet } from "react-router-dom";
import '../App.css';
export default function Client_Nav(props) {
    const [myclass, setMyclass] = useState("last-clicked");
    const [myclass1, setMyclass1] = useState("App-link");
    const [myclass2, setMyclass2] = useState("App-link");
    const [myclass3, setMyclass3] = useState("App-link");
    const [regularStyle, setRegularStyle] = useState("App-link");
    const [currentBtn, setCurrentBtn] = useState("myclass2");

    let navigate=useNavigate();
    useEffect(() => {
        // setMystyle({
        //     color: "white",
        //     fontSize:" 20px",
        //     marginRight:" 200px",
        //     paddingTop: "20px",
        //     paddingBottom: "3%",
        //     paddingLeft: "20px",
        //     paddingRight: "20px",
        //     display: "inline-block",
        //     transition:" 0.7s"
        // });
        // setLastClicked({
        //       fontSize: "30px",
        //     marginLeft:"90px",
        //     marginRight: "190px",
        //     backgroundColor: "wheat",
        //     transition: "0.12s"
        // });
        // setMyclass1(regularStyle);
        // setMyclass2(myclass);
        // setMyclass3(regularStyle);
        setStyle(currentBtn);

    }, []);
   function setStyle(current){
    switch (current) {
        case "myclass1":
            setMyclass1(myclass);
            setMyclass2(regularStyle);
            setMyclass3(regularStyle);
            break;
        case "myclass2":
            setMyclass1(regularStyle );
            setMyclass2(myclass);
            setMyclass3(regularStyle);
            break;
        case "myclass3":
            setMyclass1(regularStyle );
            setMyclass2(regularStyle);
            setMyclass3(myclass);
            break;

        default:
            break;
    }
    }
    return (
    <div>
        <div className="n">
            <nav className="navs">
                <Link className={myclass1}  onClick={()=>{
                    setStyle("myclass1");
                    setStyle("myclass1");
                    //setMyclass1(myclass);
                    // setMyclass2(regularStyle);
                    // setMyclass3(regularStyle);
                    }} to={`/client/shopingCart`}>shopping-cart</Link>

                <Link className={myclass2}  onClick={()=>{
                    setStyle("myclass2");
                    // setMyclass1(regularStyle );
                    // setMyclass2(myclass);
                    // setMyclass3(regularStyle);
                    }} to={`/client`}>product list</Link>

                <Link className={myclass3}  onClick={()=>{
                    setStyle("myclass3");
                    // setMyclass1(regularStyle );
                    // setMyclass2(regularStyle);
                    // setMyclass3(myclass);
                    }} to={`/Log_Out`}>log out</Link>
            </nav>
            <Outlet />
        </div>
      
    </div>);
}
const root = ReactDOM.createRoot(document.getElementById('root'));
