import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Client_Current_Product from "./Client_Current_Product";
import Client_Product_In_Cart from "./Client_Product_In_Cart";
import Client_Nav from "./Client_Nav";

export default function Client_Shoping_Cart(props) {
    const [products, setProducts] = useState([{}]);
    const [client, setClient] = useState("");
    const [quantity, setQuantity] = useState([]);
    const [orderid, setOrderid] = useState([]);
    const [item, setitem] = useState([]);
    const [itemQ, setitemQ] = useState([]);
    const [Allitems, setAllitems] = useState([]);
    const [itemCode, setitemCode] = useState([]);
    const [outOfStock, setOutOfStock] = useState(false);
    const [belowStock, setBelowStock] = useState(false);
    const [itemname, setitemname] = useState(products.QuantityItem);
    const [cart, setCart] = useState([]);
    const [index, setindex] = useState(-1);

    let navigate = useNavigate();
    let location = useLocation();
    useEffect(() => {
        let Allitemss = location.state.products;
        setAllitems(Allitemss);
        setProducts(Allitemss);
        showItems();
        checkStock();

    });
    async function showItems() {
        let itemCodes = products.CodeItem;
        setitemCode(itemCodes);
        if (itemCodes) {
            // AllItems = props.Allitems;
            let iteme = Allitems.filter(i => i.CodeItem == itemCode);
            setitem(iteme);
            let itemQe = iteme[0].QuantityItem;
            setitemQ(itemQe);
        }
    }

    function checkStock() {
        if (itemQ <= props.product.Quantity) {
            setOutOfStock(true);
        }
        if (itemQ === 1) {
            setBelowStock(true);
        }
    }

    async function forPaying() {
        navigate("/client/finalBill", { state: { products: products, quantity: quantity, orderid: orderid } });
    }


    // let itemss = Allitems.filter(i => i.CodeItem == itemCode);
    // setitem(itemss);
    // let itemQs = itemss[0].QuantityItem;
    // setitemQ(itemQs);
    async function increaseQ() {

        if (itemQ <= products.Quantity + 1) {
            setOutOfStock(true);
        }
        else {
            let cartForChek = JSON.parse(sessionStorage.getItem("currentuser")).cart;
            for (let index = 0; index < cartForChek.length; index++) {
                if (JSON.stringify((cartForChek[index].CodeItem)) == itemCode) {
                    cartForChek[index].Quantity++;
                    itemname++;
                }
            }
            setCart(cartForChek);
            let newClient = JSON.parse(sessionStorage.getItem("currentuser")).user_name;
            let newPass = JSON.parse(sessionStorage.getItem("currentuser")).password;
            let newData = { user_name: newClient, password: newPass, cart: cartForChek }
            window.sessionStorage.setItem("currentuser", JSON.stringify(newData))
        }

    }
    async function reduceQ() {
    }
    async function removeItem() {
    }
    return (<div>
        <Client_Nav />
        <p>shoping cart</p>
        {products.map((myproduct, index) =>
        (
            <div key={index}>
                <br></br>{props.product.ItemName}
                <p>quantity: </p>
                <p>{itemname}</p>
                {console.log(itemname)}
                {/* <Client_Product_In_Cart product={myproduct} Allitems={products} /> */}
                <button disabled={outOfStock} onClick={() => { increaseQ(index + 1) }}>+</button>
                <button disabled={belowStock} onClick={() => { reduceQ(index + 1) }}>-</button>
                <button onClick={removeItem}>remove</button>
            </div>
        ))}
        <button onClick={forPaying}>to pay</button>
    </div>);
}
