import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
export default function Client_Product_In_Cart(props) {
    const [cart, setCart] = useState([]);
    const [outOfStock, setOutOfStock] = useState(false);
    const [belowStock, setBelowStock] = useState(false);
    let itemname = props.product.QuantityItem;
    let itemCode;
    let AllItems;
    let item;
    let itemQ;
    useEffect(() => {
        // sessionStorage.setItem("currentuser",JSON.stringify({password:111,user_name:"chani",cart:{}}));
        debugger;
        // async function getCart() {
        //     let prod = await fetch("http://localhost:3678/cart/shopingCart", {
        //         method: 'POST',
        //         headers: {
        //             'Accept': 'application/json',
        //             'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify({ password: JSON.parse(sessionStorage.getItem("currentuser")).password })
        //     });
        //     let pro = await prod.json();
        //     setCart(pro);
        // }
        async function showItems() {
            itemCode = await props.product.CodeItem;
            if (itemCode) {
                AllItems = props.Allitems;
                item = AllItems.filter(i => i.CodeItem == itemCode);
                itemQ = item[0].QuantityItem;
            }
        }


        // getCart();
        showItems();
        checkStock();
       
           
     
    }, []);

    function checkStock() {
        if (itemQ <= props.product.Quantity) {
            setOutOfStock(true);
        }
        if (itemQ = 1) {
            setBelowStock(true);
        }
    }







    

    async function removeItem() {
    }

    // console.log("props" + props);


    return (<div>
        <br></br>{props.product.ItemName}
        <p>quantity: </p>
        <p>{itemname}</p>
        {console.log(itemname)};
       
        {<button onClick={removeItem}>remove</button>}
    </div>)
}
