import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";
import Nav from "./Nav"
import '../App.css';

export default function Manager_Add_Product(props) {
    const [product_name, setproductname] = useState();
    const [product_description, setproductdescription] = useState();
    const [image, setImage] = useState({ preview: '', data: '' });
    const [status, setStatus] = useState('');
    function App() {
        
        const handleSubmit = async (e) => {
            e.preventDefault();
            let formData = new FormData();
            formData.append('file', image.data);
            const response = await fetch('http://localhost:3000/image', {
                method: 'POST',
                body: formData,
            });
            if (response) {
                setStatus(response.statusText);
            }
        }

        const handleFileChange = (e) => {
            const img = {
                preview: URL.createObjectURL(e.target.files[0]),
                data: e.target.files[0],
            }
            setImage(img);
        }
        return (
            <div className='App'>
                <h1>insert a picture:</h1>
                {image.preview && <img src={image.preview} width='100' height='100' />}
                <hr></hr>
                <form onSubmit={handleSubmit}>
                    <input type='file' name='file' onChange={handleFileChange}></input>
                    <button className="btn" type='submit'>download</button>
                </form>
                {status && <h4>{status}</h4>}
            </div>);
    }



    function saveproductname(product) {
        setproductname(product);
    }

    function saveproductdescription(text) {
        setproductdescription(text);
    }

    async function save_new_product() {
        fetch("http://localhost:3000/product/save",{
            method:'POST',
            body:JSON.stringify({
                name:product_name,
                description:product_description,
                path:image.preview
            })
        })
    }
    // value={product_name}
    return (<div>
        <Nav></Nav>
        <h1 className="">put in a product name</h1>
        <input type="input" className="" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h3>put in the amount of units</h3>
        <input type="input" className="" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h3>catagory</h3>
        <input type="input" className="" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h3>code of product</h3>
        <input type="input" className="" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h3>price</h3>
        <input type="input" className="" onChange={(e) => { saveproductname(e.target.value) }} placeholder="product name"></input>
        <h3>put in a product description</h3>
        <textarea className="" onChange={(e) => { saveproductdescription(e.target.value) }} rows="10" cols="50" placeholder="product description"></textarea >
        <br></br>
        {App()}
        <br></br>
        <button  className="btn btn-outline-secondary btn-lg" onClick={save_new_product}>save</button>
    </div>)
}
