import React, { useState } from "react";
import Nav from "./Nav"
import '../App.css';
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";

export default function Manager_Current_Product(props) {
    let location=useLocation();
    let currentProduct=location.state.currentProduct;
    
    
    return (<div>
        <p>ggg!!!!!!!!!!</p>
        <img src="http://localhost:3000/picture.jpg" />
        <Nav></Nav>
        <form>
            <h4 className="">put in a product name</h4>
            <input type="input" className="" value={product_name} onChange={(e) => setproductname(e.target.value)} placeholder="product name"></input>
            <h4>put in the amount of units</h4>
            <input type="input" className="" value={amount_of_units} onChange={(e) => { saveamountofunits(e.target.value) }} placeholder="amount of units"></input>
            <h4>catagory</h4>
            <select name="catagory" onChange={(e) => { savecatagory(e.target.value) }} value={catagory}>
                <option value="1">heels</option>
                <option value="2">flat</option>
                <option value="3">sneakers</option>
                <option value="4">sandals</option>
                <option value="5">boots</option>
                <option value="6">slippers</option>
            </select>
            {/* <label>categories: </label>
                <select onChange={(e) => { categoryType(e.target.value) }} >
                    {categories.map((option, index) => (
                        <option key={option.CategoryCode} value={option.value}>
                            {option.CategoryName}
                        </option>
                    ))}
                </select> */}
            {/* <input type="number" min="1" max="6" className="" value={catagory} onChange={(e) => { savecatagory(e.target.value) }} placeholder="catagory"></input> */}
            <h4>price</h4>
            <input type="text" className="" value={price} onChange={(e) => { saveprice(e.target.value) }} placeholder="price"></input>
            <h4>put in a product description</h4>
            <textarea className="" value={product_description} onChange={(e) => { saveproductdescription(e.target.value) }} rows="10" cols="50" placeholder="product description"></textarea >
            {/* <input type="reset" value="Reset"/> */}
        </form>
        <br></br>
        {App()}
        <br></br>
        <button className="btn btn-outline-secondary btn-lg" onClick={save_new_product}>save</button>
   
    </div>);
}
