import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import Manager_Product_List from "./Manager_Product_List";
import Manager_Add_Product from "./Manager_Add_Product";
import Manager_Orderding_List from "./Manager_Orderding_List";
import Manager_Profits from "./Manager_Profits";
import { Link, Outlet } from "react-router-dom";
import '../App.css';
import ReactDOM from "react-dom/client";

export default function Client_Nav(props) {
    const [myclass, setMyclass] = useState("last-clicked");
    const [myclass1, setMyclass1] = useState("App-link");
    const [myclass2, setMyclass2] = useState("App-link");
    const [myclass3, setMyclass3] = useState("App-link");
    const [regularStyle, setRegularStyle] = useState("App-link");
    const [currentBtn, setCurrentBtn] = useState("myclass2");
    var b=b?b:"";
    const [btnClass, setBtnClass] = useState(false);
    let navigate=useNavigate();
    useEffect(() => {
        setStyle(currentBtn);
console.log(window.location.pathname);
    }, []);
   function setStyle(current){
    b=b?b:current;
    setCurrentBtn(current);
    switch (current) {
        case "myclass1":
            setMyclass1(myclass);
            setMyclass2(regularStyle);
            setMyclass3(regularStyle);
            break;
        case "myclass2":
            setMyclass1(regularStyle );
            setMyclass2(myclass);
            setMyclass3(regularStyle);
            break;
        case "myclass3":
            setMyclass1(regularStyle );
            setMyclass2(regularStyle);
            setMyclass3(myclass);
            break;

        default:
            break;
    }
   
    }
     function t(){
        navigate(`/client/shopingCart`);
      // alert("done");

    }
    return (
    <div>
        <div className="n">
            <nav className="navs">
            <button
      onClick={()=>{
        setStyle("myclass1");
       t();
           }} className={b==="myclass1"?"last-clicked":"App-link" } > button
      </button>
                <Link onClick={()=>{
                // setStyle("myclass1");
                    }} className={window.location.pathname==="/client/shopingCart"?"last-clicked":"App-link" }   to={`/client/shopingCart`}>shopping-cart</Link>

                <Link className={window.location.pathname==="/client"?"last-clicked":"App-link"}  onClick={()=>{
                  //  setStyle("myclass2");
                    }} to={`/client`}>product list</Link>

                <Link className={myclass3}  onClick={()=>{
                    setStyle("myclass3");
                      }} to={`/Log_Out`}>log out</Link>
            </nav>
            <Outlet />
        </div>
      
    </div>);
}
