import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
// import React, { Component, useEffect, useState } from 'react';
// import { useParams } from "react-router-dom";
import Our_Client from "./Our_Client";
export default function Client_Current_Product(props) {
    const [product, setProduct] = useState({});
    let navigate = useNavigate();
    let location = useLocation();
    // const [category, setCategory] = useState("");
    // let { itemCode } = useParams();
    let itemCode = location.state.CodeItem;
    let items = location.state.products;
let myproduct=location.state.myproduct;
    useEffect(() => {
        let item = items.filter(i => i.CodeItem == itemCode);
        setProduct(item[0]);
    }, []);

    let user = sessionStorage.getItem("currentuser");
    // let currentUser = JASON.parse(user.password);
    let currentUser = JSON.parse(user);




    async function addToCart() {

        let helparrray=[];
        if (currentUser.cart.length > 0) {
            //             let aaa=[];
            // animals.map((it,i)=>{
            // if(it=='pigs'){
            // aaa.push('cows');
            // }else{
            // aaa.push(it);
            // }
            // })
            // 
            let today=new Date();
            let date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
        let res=   await fetch(`http://localhost:3678/orders/add`, {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body:JSON.stringify({customerid:currentUser.password,orderprice:0,date: date,status:0})
            });
            res=await res.json();
            let istrue=false;
            currentUser.cart.map((item) => {
                if (itemCode === item.CodeItem) {
                istrue=true;
                console.log((item.QuantityItem + 1));
                // ,OrderID: itemorderid
                    helparrray.push({ CodeCategory:item.CodeCategory, CodeItem: item.CodeItem,ItemName:item.ItemName,Price:item.Price,QuantityItem:(item.QuantityItem + 1) ,description:item.description,path:item.path  });
                }
                else {
                    helparrray.push({ CodeCategory:item.CodeCategory, CodeItem: item.CodeItem,ItemName:item.ItemName,Price:item.Price,QuantityItem:(item.QuantityItem + 1) ,description:item.description,path:item.path  });
                }
            })
            // sessionStorage.setItem("currentuser", JSON.stringify({ password: password_value, user_name: user_name_value ,cart:[]}));
            console.log(helparrray);
            if(istrue){
                sessionStorage.setItem('currentuser', JSON.stringify({password:currentUser.password,user_name:currentUser.user_name,cart:helparrray}));
            }else{
                helparrray.push({CodeCategory: myproduct.CodeCategory,
                    CodeItem: myproduct.CodeItem,
                    ItemName: myproduct.ItemName,
                    Price: myproduct.Price,
                    QuantityItem: 1,
                    description: myproduct.description,
                    path:myproduct.path});
                sessionStorage.setItem('currentuser',JSON.stringify({password:currentUser.password,user_name:currentUser.user_name,cart:helparrray}));
            }
            alert("Successfully added to your cart!!");
        }else{
            helparrray.push({CodeCategory: myproduct.CodeCategory,
                CodeItem: myproduct.CodeItem,
                ItemName: myproduct.ItemName,
                Price: myproduct.Price,
                QuantityItem:1,
                description: myproduct.description,
                path:myproduct.path});
            sessionStorage.setItem('currentuser',JSON.stringify({password:currentUser.password,user_name:currentUser.user_name,cart:helparrray}));
            alert("Successfully added to your cart!!");
        }
        navigate("/client");
        console.log("addtocart");
        // let itemIC = await fetch(`http://localhost:3678/addToCart`, {
        //     method: 'POST',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json'
        //     },
        //     body: JSON.stringify({
        //         CodeItem: product.CodeItem,
        //         orderid: currentUser.password
        //     })
        // });
    }
    
    async function forShoppingCart() {
        navigate("/client/shopingCart");
    }


    return (<div>
        {/* <img src="{product[0].path}" alt="item" /> */}
        <p>{product.ItemName}</p>
        <p>{product.description}</p>
        <p>price:</p><p>{product.Price}</p>
        <button onClick={(e) => addToCart()}>add to shopping cart</button><br />
        <button onClick={forShoppingCart}>to shopping cart</button>
    </div>);
}
