            //                   password: JSON.parse(sessionStorage.getItem("currentuser")).password, 
            //                   cart: JSON.parse(sessionStorage.getItem("currentuser")).cart })
            //         });
            //         alert('updated details and exit');
            //     }

            // }
        
     
    }, []);

    function checkStock() {
        if (itemQ <= props.product.Quantity) {
            setOutOfStock(true);
        }
        if (itemQ = 1) {
            setBelowStock(true);
        }
    }







    

