import React, { useState } from "react";
import Nav from "./Nav"
import '../App.css';
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";

export default function Manager_Current_Product(props) {
    let location = useLocation();
    let currentProduct = location.state.currentProduct;
    const [product_name, setproductname] = useState(currentProduct.ItemName);
    const [product_description, setproductdescription] = useState(currentProduct.description);
    const [image, setImage] = useState({ preview: currentProduct.path, data: '' });
    const [status, setStatus] = useState('');
    const [truf, settruf] = useState(true);
    const [amount_of_units, setamountofunits] = useState(currentProduct.Quantity);
    const [catagory, setcatagory] = useState(currentProduct.codeCategory);
    // const [code, setcode] = useState("");
    const [price, setprice] = useState(currentProduct.Price);
    function App() {
        const handleSubmit = async (e) => {
            e.preventDefault();
            let formData = new FormData();
            formData.append('file', image.data);
            const response = await fetch('http://localhost:3000/image', {
                method: 'POST',
                body: formData,
            });
            if (response) {
                setStatus(response.statusText);
            }
        }
        const handleFileChange = (e) => {
            const img = {
                preview: URL.createObjectURL(e.target.files[0]),
                data: e.target.files[0],
            }
            setImage(img);
            settruf(true);
            setStatus('');
        }
        return (
            <div className='App'>
                <h1>insert a picture:</h1>
                {truf && image.preview && <img src={image.preview} width='100' height='100' />}
                <hr></hr>
                <form onSubmit={handleSubmit}>
                    <input type='file' name='file' onChange={handleFileChange}></input>
                    <button className="btn btn-outline-secondary btn-lg" type='submit'>download</button>
                </form>
                {truf && status && <h4>{status}</h4>}
            </div>
        );
    }
    function saveproductname(product) {
        setproductname(product);
    }
    function saveproductdescription(text) {
        setproductdescription(text);
    }
    function saveamountofunits(units) {
        setamountofunits(units);
    }
    function savecatagory(catagory) {
        setcatagory(catagory);
    }
    // function savecode(code) {
    //     setcode(code);
    // }
    function saveprice(price) {
        setprice(price);
    }
    async function save_new_product() {
        if (product_name == '' || product_description == '' || image.preview == '' || amount_of_units == '' || catagory == '' || price == '') {
            alert("Please fill in all input fields!!");
        }
        else {
            if (typeof product_name == "string" && isNaN(product_name) && typeof product_description == "string"
                && isNaN(product_description) && typeof image.preview == "string" && isNaN(image.preview) &&
                !isNaN(amount_of_units) && !isNaN(catagory) && !isNaN(price) && product_name.length > 0 &&
                product_description.length > 0 && image.preview.length > 0 && amount_of_units.length > 0 &&
                catagory.length > 0 && price.length > 0) {
                let codeC = await fetch(`http://localhost:3000/products`, {
                    method: 'GET'
                });
                let codeCateg = await codeC.json();
                let exsistname = false;
                codeCateg.forEach(element => {
                    if (element.ItemName === product_name) {
                        exsistname = true;
                    }
                });
                if (exsistname) {
                    alert("name exsists");
                    saveproductname('');
                }
                else {
                    let ans = await fetch("http://localhost:3000/products/save", {
                        method: 'POST',
                        headers: {
                            'Accept': 'application/json',
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            name: product_name,
                            description: product_description,
                            path: image.data.name,
                            amount: amount_of_units,
                            category: catagory,
                            price: price
                        })
                    })
                    ans = await ans.json();
                    console.log(ans);
                    // settruf(false);
                    setStatus("");
                    setImage({ preview: '', data: '' });
                    saveproductname('');
                    setproductdescription('');
                    setamountofunits('');
                    setcatagory('1');
                    // setcode('');
                    setprice('');
                }
            }
            else {
                alert('There is a problem with the input fields');
                console.log("typeof product_name: " + Number(product_name));
                if (isNaN(amount_of_units)) {
                    console.log("nan!");
                }
                console.log("typeof product_description: " + typeof product_description);
                console.log("typeof image.preview: " + typeof image.preview);
                console.log("typeof amount_of_units: " + typeof Number(amount_of_units));
                console.log("typeof catagory: " + typeof catagory);
                // console.log("typeof code: " + typeof code);
                console.log("typeof price: " + typeof price);
            }
            // settruf(false);
            // saveproductname('');
            // setproductdescription('');
            // setamountofunits('');
            // setcatagory('1');
            // // setcode('');
            // setprice('');
        }

    }

    return (<div>
        <p>ggg!!!!!!!!!!</p>
        <img src="" />
        <div>
            <Nav />
            <p>all items</p>
            {/* <div>{products.map((product) => product.ItemName)}</div> */}
            <br></br>
            <label>categories: </label>
            <select onChange={(e) => { categoryType(e.target.value) }} >
                {categories.map((option, index) => (
                    <option key={option.CategoryCode} value={option.value}>
                        {option.CategoryName}
                    </option>
                ))}
            </select>
            {/* <select onChange={(e)=>{setCategory(e.target.value)}}> */}
            {/* <select onChange={(e) => { categoryType(e.target.value) }}> */}
            {/* {getcategories()} */}
            {/* <option />  {categories.map((myproduct) =>
                    (<div key={myproduct.CategoryCode}>
                         <option>{myproduct.CategoryName}</option>
                    </div>)
                )} */}
            {/* <option>heels</option>
                <option>flat</option>
                <option>sneakers</option>
                <option>sandals</option>
                <option>boots</option>
                <option>slippers</option> */}
            {/* </select> */}
            <br></br>{products.map((myproduct) =>
                (<div key={myproduct.ItemName}>
                    {myproduct.ItemName}
                    <button onClick={() => { delete_item(myproduct.CodeItem) }}>delete</button>
                    <button onClick={() => { updating_item(myproduct.CodeItem) }}>updating</button>
                    {/* <button>add to shopping cart</button> */}
                </div>)
            )}
            {/* <button onClick={(e) => { buymyproduct(e.target.value) }}>ADD TO SHOPING CART</button> */}
        </div>
    </div>);
}
