import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import My_Manager from "./My_Manager";
import Nav from "./Nav"
import '../App.css';
export default function Manager_Product_List(props) {
    const location = useLocation();
    const [products, setProducts] = useState([]);
    const [client, setClient] = useState([]);
    const [category, setCategory] = useState("");
    const [mystyle, setMystyle] = useState({});
    useEffect(() => {
        async function getProducts() {
            let prod = await fetch("http://localhost:3000/products", {
                method: 'GET'
            });
            let pro = await prod.json();
            setProducts(pro);
            setMystyle({
                color: "#3aedff",
                fontSize: "20px", marginLeft: "330px",
                marginTop: "70PX"
            });
        }
        getProducts();
    }, []);
    // async function buymyproduct(myproduct) {
    //     let prod = await fetch("http://localhost:3000/client{}/shopingCart", {
    //         method: 'POST',
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json'
    //         },
    //         body: { myproduct }
    //     });
    //     let pro = await prod.json();
    // }
    async function delete_item(name) {
        let prod = await fetch(`http://localhost:3000/products/${name}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            // body: { myproduct }
        });
        let pro = await prod.json();
        if (pro) {
            alert("hoooraayyyyyyy!!!!!! item has been deleted!!");
        }else{
            alert("bad news :( item has failed to be deleted...:(");
        }
        getProducts();
    }
    async function categoryType(val) {
        setCategory(val);
        console.log(`val ${val}`);
        let codeC = await fetch(`http://localhost:3000/products/${val}`, {
            method: 'GET'
        });
        let codeCateg = await codeC.json();

        let catItems = await fetch(`http://localhost:3000/products/category/${codeCateg[0].CategoryCode}`, {
            method: 'GET'
        });
        let allCatItems = await catItems.json();
        console.log(allCatItems);
        setProducts(allCatItems);
    };
    return (
        <div>
            <Nav />
            <p>all items</p>
            {/* <div>{products.map((product) => product.ItemName)}</div> */}
            <br></br>
            <label>categories: </label>
            {/* <select onChange={(e)=>{setCategory(e.target.value)}}> */}
            <select onChange={(e) => { categoryType(e.target.value) }}>
                <option>heels</option>
                <option>flat</option>
                <option>sneakers</option>
                <option>sandals</option>
                <option>boots</option>
                <option>slippers</option>
            </select>
            <br></br>{products.map((myproduct) =>
            (<div key={myproduct.ItemName}>
                {myproduct.ItemName}
                <button onClick={() => { delete_item(myproduct.ItemName) }}>delete</button>
                <button>updating</button>
                {/* <button>add to shopping cart</button> */}
            </div>)
            )}
            {/* <button onClick={(e) => { buymyproduct(e.target.value) }}>ADD TO SHOPING CART</button> */}
        </div>
    )
}
